import React, { useState, useEffect } from "react";
import "../styles/App.css";
import useCookie, { setCookie } from "react-use-cookie";

function App() {
  const [currentTime, setCurrentTime] = useState(0);
  const [userinfo, setUserInfo] = useCookie("userinfo", "");
  const RemoveCookie = (locale) => {
    setCookie('userinfo', locale, {
      days: 0,
    });
  };
  useEffect(() => {
    fetch("/time")
      .then((res) => res.json())
      .then((data) => {
        setCurrentTime(data.time);
      });
  }, []);

  return (
    <div className="App">
      <header className="App-header">

        {userinfo && JSON.parse(userinfo).name}
        {userinfo && JSON.parse(userinfo).email}
        <p>The current time is {currentTime}.</p>

        <button
          type="button"
          onClick={() => {
            RemoveCookie()
            window.location.href = "/login";
          }}
        >
          Logout
        </button>
      </header>
    </div>
  );
}

export default App;
